    private void MostrarVentas() throws SQLException{
        String sql = "SELECT * FROM venta;";
        Statement V=null, C=null, S=null,E=null;
        ResultSet rsVenta = consultaV(sql, V);
        ResultSet rsCliente, rsServicio, rsEmpleado;
        DefaultTableModel m=(DefaultTableModel)tblVistaVentas.getModel();
        m.setRowCount(0);
        while(rsVenta.next()){
                rsCliente = consultaV("SELECT nombre_cliente FROM cliente WHERE id_cliente = "+rsVenta.getInt(6)+";", C);
                rsServicio = consultaV("SELECT tipo_servicio FROM servicio WHERE id_servicio = "+rsVenta.getInt(5)+";", S);
                rsEmpleado = consultaV("SELECT nombre_usuario FROM usuario WHERE id_usuario = "+rsVenta.getInt(7)+";", E);
                rsCliente.next();
                rsServicio.next();
                rsEmpleado.next();
                Object []fila={rsVenta.getInt(1), 
                    rsCliente.getString(1), 
                    rsServicio.getString(1), 
                    rsVenta.getFloat(4), 
                    rsVenta.getDate(2), 
                    rsVenta.getDate(3), 
                    rsEmpleado.getString(1)};
                m.addRow(fila);
            }
    }
    
