    private void tabla_serviciosMousePressed(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tabla_serviciosMousePressed
        int filaseleccionada;
        nombreservicio.setEnabled(true);
        precioservicio.setEnabled(true);
        try{
            filaseleccionada= tabla_servicios.getSelectedRow();
            if (filaseleccionada==-1){
                JOptionPane.showMessageDialog(null,"No se ha seleccionado ninguna fila");
            }else{
                precioservicio.setEnabled(true);
                descripcion_ser.setEnabled(true);
                DefaultTableModel modelotabla=(DefaultTableModel) tabla_servicios.getModel();  
                String nombre=(String)modelotabla.getValueAt(filaseleccionada, 0);
                Object precio=modelotabla.getValueAt(filaseleccionada, 1);
                String descripcion=(String)modelotabla.getValueAt(filaseleccionada, 2);
                nombreservicio.setText(nombre);
                precioservicio.setText(precio+"");
                descripcion_ser.setText(descripcion);
            }
        }catch (HeadlessException ex){
            JOptionPane.showMessageDialog(null, "Error: "+ex+"\nInténtelo nuevamente", " .::Error En la Operacion::." ,JOptionPane.ERROR_MESSAGE);
        }   
    }//GEN-LAST:event_tabla_serviciosMousePressed

